INSERT INTO `sys_log` VALUES ('178', '1', 'SYSTEM', 'INFO', '用户: [root] | 操作: 发布文章', '{\"isMarkdown\":[\"0\"],\"id\":[\"\"],\"title\":[\"Linux安装redis\"],\"original\":[\"on\"],\"content\":[\"<h5 style=\\\"font-family: &quot;Helvetica Neue&quot;, Roboto, Arial, &quot;Droid Sans&quot;, sans-serif; color: rgb(115, 135, 156);\\\">以下操作都是linux命令操作</h5><h3 style=\\\"font-family: &quot;Helvetica Neue&quot;, Roboto, Arial, &quot;Droid Sans&quot;, sans-serif; color: rgb(115, 135, 156);\\\">1.获取redis资源</h3><p>&nbsp; &nbsp;wget http://download.redis.io/releases/redis-4.0.8.tar.gz<br><br></p><h3>2.解压</h3><p><br>　　tar xzvf redis-4.0.8.tar.gz<br><br></p><h3>3.安装</h3><p><br>　　cd redis-4.0.8<br><br>　　make<br><br>　　cd src<br><br>　　make install PREFIX=/usr/local/redis<br><br></p><h3>4.移动配置文件到安装目录下</h3><p><br>　　cd ../<br><br>　　mkdir /usr/local/redis/etc<br><br>　　mv redis.conf /usr/local/redis/etc<br><br></p><h3>5.配置redis为后台启动</h3><p><br>　　vi /usr/local/redis/etc/redis.conf //将daemonize no 改成daemonize yes<br><br></p><h3>6.将redis加入到开机启动</h3><p><br>　　vi /etc/rc.local //在里面添加内容：/usr/local/redis/bin/redis-server /usr/local/redis/etc/redis.conf (意思就是开机调用这段开启redis的命令)<br><br></p><h3>7.开启redis</h3><p><br>　　/usr/local/redis/bin/redis-server /usr/local/redis/etc/redis.conf&nbsp;</p><p></p>\"],\"file\":[\"\"],\"coverImage\":[\"\"],\"typeId\":[\"5\"],\"tags\":[\"6\"],\"status\":[\"1\"],\"comment\":[\"on\"],\"description\":[\"redis\"],\"keywords\":[\"redis\"]}', null, '117.13.3.211', 'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/65.0.3325.181 Safari/537.36', 'Windows 10', 'Chrome', 'http://49.233.128.101:8082/article/save', 'http://49.233.128.101:8082/article/publish', '2019-10-25 15:36:44', '2019-10-25 15:36:44');
